import React, { useState, useEffect } from 'react'
import { BeatLoader } from 'react-spinners'
import { HappyThought } from './components/Posted'
import { HappyForm } from './components/Form'

const APIdata = 'https://technigo-thoughts.herokuapp.com/'



export const App = () => {
  const [thoughts, setThoughts] = useState([])
  const [postedMessage, setPostedMessage] = useState('')
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    setLoading(true)
    fetch(APIdata)
      .then(res => res.json())
      .then(json => setThoughts(json),
      setLoading(true)
      )}, [postedMessage])

  const handleFormSubmit = message => {
    fetch(APIdata, {
      method: 'POST',
      body: JSON.stringify({ message }),
      headers: { "Content-Type": "application/json" }
    })
    .then(() => setPostedMessage(message))
    .catch(err => console.log("error:", err))
  }

  const onLiked = thoughtId => {
    const updatedThoughts = thoughts.map(thought => {
      if (thought._id === thoughtId) {
        thought.hearts += 1
      }
      return thought
    })
    setThoughts(updatedThoughts)
  }

  return (
    <div>
      <HappyForm onFormSubmit={handleFormSubmit} />
      {loading ? <div className="loading"><ScaleLoader color='black' /></div> : thoughts.map(thought => (
        <HappyThought key={thought._id} thought={thought} onLiked={onLiked} />
      ))}
    </div>
  )
}
